
public class bitwise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(true & true);
		System.out.println(true & false);
		System.out.println(true | true);
		System.out.println(true | false);
		System.out.println(false | false);
		System.out.println(true ^ true);
		System.out.println(true ^ false);
		System.out.println(4&5);
		System.out.println(4|5);
		System.out.println(4^5);

	}

}
